target.sql:
             DROP TABLE IF EXISTS CAR;

             CREATE TABLE CAR (
                ID BIGINT identity,
                MAKE VARCHAR(50),
                MODEL VARCHAR(50),
                MODEL_YEAR VARCHAR(50),
                STATUS VARCHAR(10)
             );


             INSERT INTO CAR (ID, MAKE, MODEL, MODEL_YEAR, STATUS)
